waves.point_eqs_eel:
n=sample*6.283;

flip=flip+1;
flip=flip*below(flip,2);

phase=sin(n*3)*sin(n*7.9)*sin(n*16.7)*sin(n*63.5)*6;

xp=sin(n+phase)*flip;
yp=cos(n+phase)*flip;
zp=0;

//rotate on y during draw
ang=n*2+phase*0.2;
xq=xp*sin(ang) + zp*cos(ang);
yq=yp;
zq=xp*cos(ang) - zp*sin(ang);

//rotation;
ang=time*0.1;
xp=xq*sin(ang) + zq*cos(ang);
yp=yq;
zp=xq*cos(ang) - zq*sin(ang);

ang=time*0.17;
xq=xp;
yq=yp*sin(ang) + zp*cos(ang);
zq=yp*cos(ang) - zp*sin(ang);


//push into viewspace
zq=zq+3.1;


//project into screenspace
xs=xq/zq;
ys=yq/zq;


x=xs+0.5;
y=ys*1.3+0.5;
a=(1-flip)*0.05;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.034);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = ((texture (sampler_main, uv).xyz * 1.5) - 0.1);
 ret_1.y = (ret_1 * 0.7).y;
 ret_1.x = (((texture (sampler_blur1, uv).xyz * scale1) + bias1).x * 2.0);
 ret_1.z = (((texture (sampler_blur2, uv).xyz * scale2) + bias2).z * 3.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.034;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = ret*1.5 - 0.1;

    float4 g;
    float3 d = float3(texsize.zw * 4, 0);
    g.x = lum(GetBlur1(uv + d.xz));
    g.y = lum(GetBlur1(uv - d.xz));
    g.z = lum(GetBlur1(uv + d.zy));
    g.w = lum(GetBlur1(uv - d.zy));

    ret *= 0.7;
    ret.x += saturate(g.x-g.y)*5;
    ret.z += saturate(g.y-g.x)*5;
    //ret.xy += (g.xz-g.yw)*3;

ret.x = GetBlur1(uv).x*2;
ret.z = GetBlur2(uv).z*3;

}
