warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1 = (texsize.zw * 4.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (pow ((
   (abs(((
     ((texture (sampler_blur3, uv).xyz * scale3) + bias3)
    * 2.0) - texture (sampler_main, uv).xyz)) * 1.8)
  + 0.12), vec3(1.8, 1.8, 1.8)) * ((
   dot ((((
     (texture (sampler_blur2, (uv + (vec2(1.0, 0.0) * tmpvar_1))).xyz * scale2)
    + bias2) - (
     (texture (sampler_blur2, (uv - (vec2(1.0, 0.0) * tmpvar_1))).xyz * scale2)
    + bias2)) + ((
     (texture (sampler_blur2, (uv + (vec2(0.0, 1.0) * tmpvar_1))).xyz * scale2)
    + bias2) - (
     (texture (sampler_blur2, (uv - (vec2(0.0, 1.0) * tmpvar_1))).xyz * scale2)
    + bias2))), vec3(0.32, 0.49, 0.29))
  * 4.0) + 1.0));
