warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{
float2 d = texsize.zw * 4;
float3 dx = ( GetBlur2(uv + float2(1,0)*d) - GetBlur2(uv - float2(1,0)*d) );
float3 dy = ( GetBlur2(uv + float2(0,1)*d) - GetBlur2(uv - float2(0,1)*d) );

ret = pow(abs(GetBlur3(uv)*2-GetPixel(uv))*1.8+0.12,1.8);
//ret = GetBlur3(uv)*GetBlur1(uv)*4;
//ret = GetPixel(uv);
ret *= lum(dx+dy)*4+1;
}
