waves.point_eqs_eel:
x = .5+(.04+(mid_att*.03))*cos(sample*6.28);
y = .5+(.04+(mid_att*.03))*sin(sample*6.28);
r = abs(sin(sample*20-time));
b = abs(sin((sample+.33)*20-time*.667));
g = abs(sin((sample+.66)*20-time*1.44));

frame_eqs_eel:
warp = 0;
wave_r = wave_r + .4*sin(time*.333)+bass_att*.2;
wave_g = wave_g + .4*sin(time*.555)+treb_att*.2;
wave_b = wave_b + .4*sin(time*.444)+mid_att*.2;
bassspin = if(above(bass*.09,bassspin),bassspin+.002,bassspin-.002);
bassspin = bassspin*above(bassspin,0);
bassc = bassc+bassspin;
q1 = bassc;

warp:
shader_body { 
 vec2 uv2_1;
 float tmpvar_2;
 tmpvar_2 = (ang * 10.0);
 float tmpvar_3;
 tmpvar_3 = (q1 * 5.0);
 uv2_1.x = (uv.x + (0.003 * sin(
   (tmpvar_2 + tmpvar_3)
 )));
 uv2_1.y = (uv.y + (0.003 * cos(
   (tmpvar_2 + tmpvar_3)
 )));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_fw_main, uv2_1).xyz - 0.005) * 0.97);
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 blr_1;
 vec3 ret_2;
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_main, uv);
 ret_2 = (tmpvar_3.xyz * (tmpvar_3.xyz + 0.75));
 vec3 tmpvar_4;
 tmpvar_4 = ((texture (sampler_blur2, uv).xyz * scale2) + bias2);
 blr_1 = (tmpvar_4 - (abs(
   (tmpvar_4 - 1.0)
 ) * 0.1));
 ret_2 = (mix (ret_2, blr_1, (0.5 + blr_1)) + blr_1);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_2;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    float2 uv2 = uv;
    uv2.x = uv.x + (.003*sin((ang*10)+(q1*5)));
    uv2.y = uv.y + (.003*cos((ang*10)+(q1*5)));
    ret = tex2D( sampler_fw_main, uv2 ).xyz;
    // darken (decay) over time
   ret = (ret - .005)*0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
  ret = tex2D(sampler_main,uv).xyz;
  ret = ret * (ret+.75);
  float3 blr = GetBlur2(uv);
  blr = blr - abs(blr - 1)*.1;
  ret = lerp(ret,blr,.5+blr)+blr;
}
