    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    float3 ret2 = tex2D( sampler_fc_main, uv ).xyz;
    // darken (decay) over time
    ret += ret - GetBlur2(uv);
    ret = lerp(ret2,ret,.07);
    ret = (ret - .005)*0.995; //or try: ret -= 0.004;
