warp_hlsl:
shader_body
{

   // sample previous frame
ret = tex2D( sampler_fc_main, lerp(uv,uv_orig,.15)).xyz;

ret.x += frac(GetPixel(uv+float2(1,2)*texsize.zw*GetPixel(uv).x).x);
ret.x -= GetBlur3(uv).x;

ret.y += frac(GetPixel(uv+float2(2,-2)*texsize.zw*GetPixel(uv).y).y);
ret.y -= GetBlur3(uv).y;

ret.z += frac(GetPixel(uv+float2(-2,-1)*texsize.zw*GetPixel(uv).z).z);
ret.z -= GetBlur3(uv).z;


    // darken (decay) over time
    ret = (ret*0.95)-.002; //or try: ret -= 0.004;
}
