waves.frame_eqs_eel:
tic = min(time-tir,.1);
tir = time;
ti = ti + tic*.5 + below(vr+vg+vb,.4)*tic*8;


vr = .75 + .25*sin(ti*1.132 + 1);
vg = .75 + .25*sin(ti*1.121 + 1);
vb = .75 + .25*sin(ti*1.187 + 1);
ar=vr;
ag=vg;
ab=vb;

waves.point_eqs_eel:
sp = sample*10;
ti = time;


ox = (int(rand(1001))*.001 + int(rand(1001))*.001 - int(rand(1001))*.001 - int(rand(1001))*.001);
oy = (int(rand(1001))*.001 + int(rand(1001))*.001 - int(rand(1001))*.001 - int(rand(1001))*.001);
oz = (int(rand(1001))*.001 + int(rand(1001))*.001 - int(rand(1001))*.001 - int(rand(1001))*.001);

dis = pow(ox*ox + oy*oy + oz*oz,.5);

vol = (value1+value2)*2;

ox = sign(ox)*pow(ox,4)*vol;
oy = sign(oy)*pow(oy,4)*vol;
oz = sign(oz)*pow(oz,4)*vol;

a = (3.464 - dis)*.57735*.3;

xang = time*.154;
yang = time*.103;
zang = time*.118;


fov = .5;

mx = ox*cos(yang) + oz*sin(yang);
mz = - ox*sin(yang) + oz*cos(yang);
ox = mx;
oz = mz;
mx = ox*cos(zang) - oy*sin(zang);
my = ox*sin(zang) + oy*cos(zang);
ox = mx;
oy = my;
my = oy*cos(xang) - oz*sin(xang);
mz = oy*sin(xang) + oz*cos(xang);
oy = my;
oz = mz;

oz = (oz - int(oz*.2)*5 - 5)*2;
x = ox*fov/oz + 0.5;
x = (x-.5)*0.75 + 0.5;
y = oy*fov/oz + 0.5;

init_eqs_eel:
mv_x=64;mv_y=48;
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
