warp_hlsl:
#define sampler_pic sampler_prayerwheel
sampler2D sampler_pic;

shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*1;

    // mix in img...
    float3 pic = tex2D(sampler_pic, uv_orig).xyz;
    float lum = lum(pic);
    float use_it = abs(lum - frac((bass+mid+treb)*0.2));
    use_it = saturate(1 - use_it*33);
    ret = lerp(ret, pic, use_it.xxx);

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
}

comp_hlsl:
shader_body
{
    float2 uv2;

    float4 c = float4(1,0.4,0.15,0.1);

    uv2 = uv + float2(1,0)*texsize.zw;
    float gx1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(-1,0)*texsize.zw;
    float gx2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    uv2 = uv + float2(0,1)*texsize.zw;
    float gy1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
