frame_eqs_eel:
vol=(bass+mid+treb_att)*.3333;

redsine=.5+.25*bass*sin(time*3);
greensine=.5+.25*mid*sin(time*2);
bluesine=.5+.25*treb*sin(time);

redif=if(above(bass,1.2),redsine,if(above(redif,.95),0,redif*.85));
greenif=if(above(mid,1.2),greensine,if(above(greenif,.95),0,greenif*.85));
blueif=if(above(treb,1.2),bluesine,if(above(blueif,.95),0,blueif*.85));

wave_r=redif;
wave_g=greenif;
wave_b=blueif;

q1=vol;

