warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1 = normalize((uv - 0.5));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (((
   ((texture (sampler_main, (uv + (texsize.zw * 
     (tmpvar_1 * -4.5)
   ))) * 0.19) + (texture (sampler_main, (uv + (texsize.zw * 
     (tmpvar_1 * -1.5)
   ))) * 0.31))
  + 
   (texture (sampler_main, (uv + (texsize.zw * (tmpvar_1 * 1.5)))) * 0.31)
 ) + (texture (sampler_main, 
   (uv + (texsize.zw * (tmpvar_1 * 4.5)))
 ) * 0.19)) - 0.009).xyz;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{  
    // Really smooth RADIAL BLUR:
    float2 v1 = normalize(uv - 0.5);
    //float2 v1 = float2(3,0);               // or for an aggressive horiz. blur
    //float2 v1 = normalize(uv - 0.5).yx * float2(-1,1) * 2;
    ret   = (  tex2D( sampler_main, uv + texsize.zw*(v1*-4.5) )*0.19
                 + tex2D( sampler_main, uv + texsize.zw*(v1*-1.5) )*0.31
                 + tex2D( sampler_main, uv + texsize.zw*(v1* 1.5) )*0.31
                 + tex2D( sampler_main, uv + texsize.zw*(v1* 4.5) )*0.19
                ) - 0.009;              
}
