frame_eqs_eel:
warp = 0;

tic = min(time-tin,.1);
tin = time;

vol = (bass_att + treb_att + mid_att)*.333333;

ra = 1/tic*.25;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);
vav = tic*(vav*(1/tic - ra) + ra*(bass+treb+mid)*.33333);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;
vt = vt + tic*vav;

sp = abs(vav - slide)*.1;
slide = if(above(slide,vav),slide-tic*sp,slide+tic*sp) + (1-toc)*vav*.2;
toc = 1;


q1 = vt; // shift along waveform
//q1 = 0;
q2 = .1 + min(bass_avg*.1,.4); // displacement of shape echo;
q3 = .5; // shape tex_zoom

bend = (mid_avg + treb_avg)*.5*.1;

tri = if(above(time - tr,.1),below(bend,.05),0);
sw = if(tri,1-sw,sw);
tr = if(tri,time,tr);
bend = max(0,bend - .05);

q4 = bend*(1-2*sw)*2; // shape tex_ang

cou = treb - treb_avg;

monitor = q4;

