init_eqs_eel:
beatrate=1;
avgbeatrate=1;
bbtime=1;
lbbtime=1;
avgdb=1;
fakebeatcount=0;

frame_eqs_eel:
db=bass-lbass;
lbass=bass;
vol=1+bass+mid+treb;

rs=above(abs(db),maxdb);
drs=1-rs;

beatrate=1*rs+beatrate*drs;
avgbeatrate=1*rs+avgbeatrate*drs;
bbtime=1*rs+bbtime*drs;
lbbtime=1*rs+lbbtime*drs;
avgdb=1*rs+avgdb*drs;
fbc=fbc*drs+1*rs;
bc=bc*drs+1*rs;
faketime=1*rs+faketime*drs;
lftime=1*rs+lftime*drs;


avgdb=avgdb*.999+abs(db)*.001;

rawbeatb=above(db*fakebeatb,avgdb*1.3);
beatb=rawbeatb*above(time-lbbtime,.1*avgbeatrate);
maxdb=max(maxdb*.9999,abs(db));

bbtime=time*beatb;

beatrate=beatb*(bbtime-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.999+beatrate*.001)+(1-beatb)*avgbeatrate;


lbbtime=time*beatb+(1-beatb)*lbbtime;


faketime=faketime+avgbeatrate*.005;
//faketime=0;
fakebeatb=cos(faketime)*.5+.5;
fb=above(fakebeatb,.9999)*above(faketime-lftime,.5*avgfbr);
fbr=(1-fb)*fbr+fb*(fbr*.99+(faketime-lftime)*.01);
avgfbr=avgbr*(1-fb)+fb*(avgfbr*.99+fbr*.01);

fbc=fbc+fb;
lftime=faketime*fb+(1-fb)*lftime;



bc=bc+beatb;

monitor=beatb;
q1=beatb;//equal((fbc-1)%4,0)*fb;
q2=db;
q3=fakebeatb;
q4=avgbeatrate;
q5=equal(bc%4,0)*beatb;
q6=equal(bc%16,0)*beatb;
q7=rawbeatb;

decay=.99;
warp=0;
wrap=1;
worp=0;

dx=.005;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_fc_main, (uv + (vec2(0.0, 8.0) * 
   (((texture (sampler_blur1, uv).xyz * scale1) + bias1).xy * texsize.zw)
 ))).xyz - 0.01);
 ret_1 = (ret_1 - (0.05 * float(
   ((float(mod (frame, 32.0))) == 0.0)
 )));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec2 uv3_2;
 vec2 uv2_3;
 vec3 ret_4;
 uv_1 = (uv - 0.5);
 uv_1 = (uv_1 * aspect.xy);
 uv2_3.x = ((uv_1.x * 0.0007962743) - (uv_1.y * 0.9999997));
 uv2_3.y = ((uv_1.x * 0.9999997) + (uv_1.y * 0.0007962743));
 uv3_2.x = ((uv_1.x * -0.00238894) - (uv_1.y * -0.9999971));
 uv3_2.y = ((uv_1.x * -0.9999971) + (uv_1.y * -0.00238894));
 ret_4 = (max (max (
   max (texture (sampler_main, (uv_1 + 0.5)).xyz, texture (sampler_main, (uv2_3 + 0.5)).zxy)
 , texture (sampler_main, 
   (uv3_2 + 0.5)
 ).yzx), texture (sampler_main, (
   (1.0 - uv_1)
  + 0.5)).zyx) * 1.3);
 ret_4 = (ret_4 * pow (hue_shader, vec3(4.0, 4.0, 4.0)));
 ret_4 = (ret_4 + ((
   ((((texture (sampler_blur1, 
     (uv_1 + 0.5)
   ).xyz * scale1) + bias1) + ((texture (sampler_blur1, 
     (uv2_3 + 0.5)
   ).xyz * scale1) + bias1).zxy) + ((texture (sampler_blur1, (uv3_2 + 0.5)).xyz * scale1) + bias1).yzx)
  + 
   ((texture (sampler_blur1, (-(uv_1) + 0.5)).xyz * scale1) + bias1)
 .zyx) / 4.0));
 ret_4 = (ret_4 * dot (ret_4, hue_shader.zxy));
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_4;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    uv+=GetBlur1(uv)*texsize.zw*8*float2(0,1);
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken
    ret-=.01;
    ret-=0.05*(frame%32==0);
}

comp_hlsl:
shader_body
{
    //rotation code by geiss, mods by me
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 1.57;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 4.71;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).zxy);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).yzx);
    ret = max(ret, tex2D(sampler_main,(1-uv)+0.5).zyx);

    ret *= 1.3;
    ret*=pow(hue_shader,4);
    ret+=(GetBlur1(uv+0.5)+GetBlur1(uv2+0.5).zxy+GetBlur1(uv3+0.5).yzx+GetBlur1(uv*float2(-1,-1)+0.5).zyx)/4;
    ret*=dot(ret,hue_shader.zxy);
}
