pixel_eqs_eel:
du = x*2-1 - q1;
dv = y*2-1 - q2;
dist = sqrt(du*du+dv*dv);
ang2 = atan2(du,dv);
mult = 0.012/(dist+0.4);
dx = mult*sin(ang2-1.5);
dy = mult*cos(ang2-1.5);
