warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // motion blur:
    // 'v' points exactly one pixel, in the direction of motion
    float2 v = normalize(uv-uv_orig)*texsize.zw;
    float3 s;
    ret = max(ret, tex2D(sampler_main, uv+v*-1)*0.90);
    ret = max(ret, tex2D(sampler_main, uv+v* 1)*0.97);
    ret = max(ret, tex2D(sampler_main, uv+v* 2)*0.97);
    ret = max(ret, tex2D(sampler_main, uv+v* 3)*0.90);

    // darken over time
    ret *= 0.92;

    // add noise
    //float2 uv_noise = uv*texsize_noise_lq.zw*texsize.xy + rand_frame.xy;
    //ret += (tex2D(sampler_noise_lq, uv_noise)*2-1)*0.02;
}

