frame_eqs_eel:
warp = 0;
wave_r = bass_att*.3;
wave_g = treb_att*.3;
wave_b = mid_att*.3;
ob_r = 0.5+0.5*sin(time*5.12);
ob_b = 0.5+0.5*sin(time*6.112);
ob_g = 0.5+0.5*sin(time*7.212);
q1 = zoom + pow((bass+bass_att),3)*.005-.02;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz - (tmpvar_2.xyz * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}
