warp_hlsl:
shader_body
{
    float2 uv2 = uv - 0.5;
    float2 uv3;
    uv3.x = (uv2.x* q1 + uv2.y*q2)*0.97 + 0.5;
    uv3.y = (uv2.x*-q2 + uv2.y*q1)*0.97 + 0.5;

    ret.x = tex2D( sampler_main, lerp(uv,uv3,0.0) ).x;
    ret.y = tex2D( sampler_main, lerp(uv,uv3,0.5) ).y;
    ret.z = tex2D( sampler_main, lerp(uv,uv3,1.0) ).z;

    // .x = fastest layer, .z = slowest
    // slowly shift stuff toward the front, over time:
    ret += float3(-ret.x,ret.x-ret.y,ret.y-ret.z)*0.03;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
