warp_hlsl:
shader_body
{
    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q29
                     );
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y += (noise9).x*.5; 
if(ret.y> q22*q11         && ret.y<= q25*q12        )ret.z += (noise9).y*.5; 
if(ret.z> q23*q12         && ret.z<= q26*q13        )ret.x += (noise9).z*.5; 
    ret.z = tex2D( sampler_main, (uv-0.5)*0.95+0.5 ).z;

    // .x = fastest layer, .z = slowest
    // slowly shift stuff toward the front, over time:
    ret += float3(-ret.x,ret.x-ret.y,ret.y-ret.z)*0.03, (bass_att-1)*0.1;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
//    ret -= 0.003;
    //ret *= 0.99;
    //ret *= 0.97*saturate( length(uv - uv_orig)*200 );  // center only
ret = ret * (0.95 + (sin(mid)*0.05));
}

