warp:
shader_body { 
 vec3 ret_1;
 ret_1 = ((texture (sampler_blur3, uv).xyz * scale3) + bias3);
 float tmpvar_2;
 tmpvar_2 = (0.01 * time);
 ret_1 = (texture (sampler_main, mix (uv, (
   (uv * (1.0 - (0.05 * texture (sampler_noise_lq, (
