 ret_1 = (ret_1 * (0.25 + (0.75 * ret_1)));
 ret_1 = (dot (ret_1, vec3(0.32, 0.49, 0.29)) * ((hue_shader * 8.0) - 6.25));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

