frame_eqs_eel:
warp = 0;

// <<-- Stahlregen's (off)beat detection 
// frame counter for average
count = count+1;
// counters of the bass/treb/mid values for average
bc = bc+bass;
tc = tc+treb;
mc = mc+mid;
// checking for impact: highest of bass/mid/treb has impact
bimp = if(above(3.25*bass,bass+treb+mid),1,0);
timp = if(above(2.75*treb,bass+treb+mid),1,0);
mimp = if(above(2.75*mid,bass+treb+mid),1,0);
// checking for beats: compare adjusted buffered 
// vs. average value and check for impact
is_beat = if(above(.75*bass,bc/count),1*bimp,
if(above(.75*treb,tc/count),1*timp,
if(above(.75*mid,mc/count),1*mimp,
0))); 
// restricting max number of beats per second (fraction of fps)
is_beat = if(above(reha,0),0,is_beat);
reha = if(is_beat,fps*.25,if(below(reha,1),0,reha-1));
// end of (off)beat detection -->

fade = if(is_beat,200+fade,.9*fade-1);

fade = if(below(fade,1),0,fade);

zoom = 1.05+.001*fade;

wave_mystery = -.375+.00025*fade;

wave_r = .5+.5*sin(time);
wave_g = .5+.5*sin(time*1.53);
wave_b = .5+.5*sin(time*2.02);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (mix (texture (sampler_main, uv).xyz, (
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1), vec3((0.05 + 
   (0.075 * bass)
 ))) - 0.001);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = texture (sampler_main, uv).xyz;
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = lerp(tex2D( sampler_main, uv ).xyz,GetBlur1(uv),.05+.075*bass);

    // darken (decay) over time
    ret = ret-0.001;
}

