warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1);
 vec2 theta_3;
 theta_3 = (3.0 * (ret_2.zz + ret_2.yx));
 uv_1 = (uv_1 + ((-0.05 * 
   (sin(theta_3) / cos(theta_3))
 ) - 0.025));
 ret_2 = (texture (sampler_main, uv_1).xyz + ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1));
 vec3 tmpvar_4;
 tmpvar_4 = clamp (ret_2, 0.0, 1.0);
 ret_2 = (tmpvar_4 * (1.25 * tmpvar_4));
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_2;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{

    uv = 0.05 + 0.9*uv;
    ret = GetBlur1(uv);
    uv += -.05*tan(3*(ret.zz+ret.yx))-.025;

    ret = tex2D(sampler_main,uv);
    ret += GetBlur1(uv);

    // palettize:
 //   ret = pow(lum(ret), float3(0.3, .76, 0.87));

    // invert
    ret = saturate(ret);
    ret *= 1.25*ret;
    //ret.xyz *= 2; // a little bit of overbright
}
