frame_eqs_eel:
warp = 0;
wave_r = wave_r + .5*sin(time*333) + bass*.3;
wave_g = wave_g + .5*sin(time*222) + treb*.3;
wave_b = wave_b + .5*sin(time*111) + mid*.3;
rot = .4*sin(mid_att*.05);

warp:
shader_body { 
 vec2 uv_1;
 vec2 tmpvar_2;
 tmpvar_2 = normalize((uv_orig - 0.5));
 float tmpvar_3;
 tmpvar_3 = (time * 7.0);
 uv_1 = (mix (uv_orig, uv, vec2((
   clamp ((((bass / bass_att) - 0.9) * 13.0), 0.0, 1.0)
  * 0.3))) + ((
   (tmpvar_2 * texsize.zw)
  * 
   cos(((rad * 170.0) - tmpvar_3))
 ) * 2.0));
 uv_1 = (uv_1 + ((
   (vec2(5.0, -5.0) * texsize.zw)
  * 
   cos(((ang * 30.0) - tmpvar_3))
 ) * tmpvar_2.yx));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_main, uv_1).xyz - 0.004) * 0.83);
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.4));
 vec3 tmpvar_2;
 tmpvar_2.x = (1.7 - (bass * 0.5));
 tmpvar_2.y = (1.7 - (treb * 0.5));
 tmpvar_2.z = (1.7 - (mid * 0.5));
 vec3 tmpvar_3;
 tmpvar_3 = pow (pow (ret_1, vec3(0.5, 0.8, 1.7)), tmpvar_2);
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    float t = saturate((bass/bass_att-0.9)*13);
    uv = lerp(uv_orig, uv, t*0.3);

    float2 v1 = normalize(uv_orig-0.5);
    float2 v2 = v1.yx * float2(1,-1);

    float z = length(texsize.zw)*450;

    uv.xy += v1*texsize.zw * cos(rad*170 - time*7)*2;
    uv.xy += v2*texsize.zw * cos(ang*30 - time*7)*5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;



    // darken (decay) over time
    ret = (ret - 0.004)*0.83;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret += GetBlur1(uv)*0.4;

    // palettize:
    ret = pow(ret, float3(0.5, 0.8, 1.7));
    ret = pow(ret, float3(1.7-(bass*.5), 1.7-(treb*.5), 1.7-(mid*.5)));

    //ret.xyz *= 2; // a little bit of overbright
}
