warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // motion blur by Geiss
    // 'v' points exactly one pixel, in the direction of motion
    float2 v = normalize(uv-uv_orig)*texsize.zw;
    float3 s;
    ret = max(ret, tex2D(sampler_main, uv+v)*0.97);
    ret = max(ret, tex2D(sampler_main, uv-v)*0.97);
    ret = max(ret, tex2D(sampler_main, uv+v*2)*0.90);
    ret = max(ret, tex2D(sampler_main, uv-v*2)*0.90);

    // darken (decay) over time
    ret = (ret-0.005)*0.98; //or try: ret -= 0.004;
}

