frame_eqs_eel:
warp=0;
dx=-0.0005;
dy=-0.0005;
wave_x = 0.5+0.3*sin(bass+treb+mid);
wave_r = 1 + sin(-wave_x*6.28);
wave_g = abs(sin(2*wave_x*6.28));
wave_b = sin(wave_x*6.28);
treb_effect = if(above(treb_att,1.4),pow(0.99,treb_att),1);
net_effect = if(above(bass_att,0.8*treb_att),1,treb_effect);
zoom = net_effect*1.065;
rot = rot + rot_residual;
bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);
shift = (tan(time*7)) -0.05;
shift = if(above(shift,0),0,if(below(shift,-0.1),-0.1,shift));
rot_residual = if(equal(bass_thresh,2),shift,rot_residual);

warp:
shader_body { 
 vec4 tmpvar_1;
