}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (pow ((
   ((texture (sampler_blur2, uv).xyz * scale2) + bias2)
  / bass_att), vec3((1.0 - 
   dot (clamp (pow ((
     ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
    / treb_att), vec3((1.0 - 
     dot (clamp (texture (sampler_main, uv).xyz, 0.0, 1.0), vec3(0.32, 0.49, 0.29))
   ))), 0.0, 1.0), vec3(0.32, 0.49, 0.29))
 ))) - (roam_sin.xyz * roam_cos.zxy));
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}
