frame_eqs_eel:
wave_r = wave_r + 0.3*(0.6*sin(time*1.11)+0.4*sin(time*1.31));
wave_g = wave_g + 0.5*(0.6*sin(time*1.15)+0.4*sin(time*3.51));
wave_b = wave_b + 0.4*(0.6*sin(time*1.19)+0.4*sin(time*5.71));
warp = oldwarp + 0.1*bass;
oldwarp = warp*0.1;
monitor = warp

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (uv + texsize.zw)).xy - 0.1)
  * 0.005))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   (0.5 - uv)
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * 2.26);
 ret_1 = (ret_1 * (0.47 + (0.53 * hue_shader)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-.1) * 0.005;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.26; //gamma
    ret *= 0.47 + 0.53*hue_shader; //old hue shader effect
}
