frame_eqs_eel:
dec=pow(.98,60/fps);
num0=numbeat*(abs(num-num_att)+pump)+bnot(numbeat)*num0;
pump=numbeat*(sin(t)*.1+.1)+bnot(numbeat)*pump;
pump=pump*dec;

num_b0=num_b;
num=abs((bass-bass_att)+(mid-mid_att)+(treb-treb_att));
num_att=num_att*.95+num*.05;
num_b=above(num,1-pump)*above(abs(num-num_att),num0);
numbeat=bnot(equal(num_b0,num_b));

t=t+num*(1-dec);
bc=bc+numbeat;

wave_r=.5+.5*sin(t*.36+time*1.2);
wave_g=.5+.5*sin(t*.18+time*1.5);
wave_b=.5+.5*sin(t*.29+time*1.6);

q1=t;
q2=bc;
q3=1+sin(time+t*3+bc*0.2);
q4=1+cos(time+t*2+bc*0.4);

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_fc_main, (uv + (vec2(0.0, 8.0) * 
   (((texture (sampler_blur1, uv).xyz * scale1) + bias1).xy * texsize.zw)
 ))).xyz - 0.01);
 ret_1 = (ret_1 - (0.05 * float(
   ((float(mod (frame, 32.0))) == 0.0)
 )));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (1.0 - uv)).xyz, vec3(0.5, 0.5, 0.5)) * 1.33);
 ret_1 = (ret_1 * ((
   mix (hue_shader, hue_shader.zxy, vec3(rad))
  * 2.0) - 1.0));
 vec3 tmpvar_2;
 tmpvar_2 = pow (mix (vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), ret_1, vec3(0.6, 0.6, 0.6)), vec3(0.3, 0.8, 1.5));
 ret_1 = (tmpvar_2 * dot (tmpvar_2, hue_shader.yzx));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    uv+=GetBlur1(uv)*texsize.zw*8*float2(0,1);
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken
    ret-=.01;
    ret-=0.05*(frame%32==0);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret=lerp(ret,GetPixel(1-uv),0.5); //video echo
    ret*= 1.33; //gamma
    ret*=lerp(hue_shader,hue_shader.zxy,rad)*2-1; //mixed hue-shader
    ret=lerp(lum(ret),ret,0.6); //half de-saturated
    ret=pow(ret,float3(0.3,0.8,1.5)); //palletize
    ret*=dot(ret,hue_shader.yzx); //add some contrast
}
