warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_fc_main, uv).xyz * 0.98);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = mix (vec3(dot ((1.0 - tmpvar_1.xyz), vec3(0.32, 0.49, 0.29))), (dot (
   (1.0 - tmpvar_1.xyz)
 , vec3(0.32, 0.49, 0.29)) * (
   (hue_shader * 8.0)
  - 7.25)), vec3(fract((
   (rad * 10.0)
  + q1))));
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.98; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{

float3 hue_plus = hue_shader*8-7.25;
ret = lerp(lum(1-GetPixel(uv)),lum(1-GetPixel(uv))*hue_plus,frac(rad*10+q1));

    ret *= 1.00; //gamma
}
