// to access a texture on disk: make a sampler.  This example loads "noise_lq.{tga/jpg/etc}".

     // auto-binds; .xy = (w,h); .zw = (1/w,1/h)

shader_body 
{
    uv.x += 0.02 * (tex2D( sampler_noise_lq, 
                         uv*0.050*texsize_noise_lq.zw*texsize.xy*2
                         + float2(roam_cos.x,roam_sin.x)*0.1 ).y*2-1)*aspect.x;
    uv.x += 0.04 * (tex2D( sampler_noise_lq, 
                         uv*0.025*texsize_noise_lq.zw*texsize.xy*0.5
                         + float2(roam_sin.y,-roam_cos.y)*0.03  ).z*2-1)*aspect.x;

    ret = tex2D( sampler_main, uv ).xyz;

    // darken over time
    // cool idea: different falloff rates for the 3 colors-
    ret *= float3(0.99,0.98,0.97);

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret.xyz += (tex2D(sampler_noise_lq, dither_uv).x-0.5)/256.0*2; 
}

