frame_eqs_eel:
wave_r = 0.3;
wave_g = 0.25;
wave_b = .6+ 0.40*( 0.60*sin(1.251*time) + 0.40*sin(1.055*time) );

rot = rot + 0.010*( 0.60*sin(0.381*time) + 0.40*sin(0.579*time) );

decay = decay - 0.01*equal(frame%6,0);

bass_thresh = above(bass_att,bass_thresh)*2 + (1-above(bass_att,bass_thresh))*((bass_thresh-1.3)*0.96+1.3);

dx_residual = (equal(bass_thresh,2)*0.016*sin(time*7) + (1-equal(bass_thresh,2))*dx_residual)*0.4;
dy_residual = (equal(bass_thresh,2)*0.012*sin(time*9) + (1-equal(bass_thresh,2))*dy_residual)*0.4;

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz * 0.85);
 ret_1 = (ret_1 - 0.002);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (abs((texture (sampler_main, uv).xyz + 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 )) * 32.0);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz*.85;

    // darken over time
    ret -= 0.002;
}

comp_hlsl:
shader_body         
{
        float3 crisp = tex2D(sampler_main, uv).xyz;
        float3 blurry = GetBlur1(uv);
        ret = abs( crisp + blurry )*32;
}
