    float srad = sqrt(rad)+ 0.05;

    ret = tex2D(sampler_main, uv).xyz;
    ret = ret+max(ret, tex2D(sampler_main, uv + .001*float2(cos(uv.x*37),sin(uv.y*29))).xyz);

    float lum = frac(ret.xy);

    ret *= lum/srad;
