frame_eqs_eel:
dx=-0.0005;
dy=-0.0005;
vol = (bass+mid+att)/6;
xamptarg = if(equal(frame%15,0),min(0.5*vol*bass_att,0.5),xamptarg);
xamp = xamp + 0.5*(xamptarg-xamp);
xdir = if(above(abs(xpos),xamp),-sign(xpos),if(below(abs(xspeed),0.1),2*above(xpos,0)-1,xdir));
xaccel = xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xspeed = xspeed + xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xpos = xpos + 0.001*xspeed;
wave_x = xpos + 0.5;
yamptarg = if(equal(frame%15,0),min(0.3*vol*treb_att,0.5),yamptarg);
yamp = yamp + 0.5*(yamptarg-yamp);
ydir = if(above(abs(ypos),yamp),-sign(ypos),if(below(abs(yspeed),0.1),2*above(ypos,0)-1,ydir));
yaccel = ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
yspeed = yspeed + ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
ypos = ypos + 0.001*yspeed;
wave_y = ypos + 0.5;
wave_r = wave_r + 0.350*( 0.60*sin(0.980*time) + 0.40*sin(1.047*time) );
wave_g = wave_g + 0.350*( 0.60*sin(0.835*time) + 0.40*sin(1.081*time) );
wave_b = wave_b + 0.350*( 0.60*sin(0.814*time) + 0.40*sin(1.011*time) );
rot = rot + 0.030*( 0.60*sin(0.381*time) + 0.40*sin(0.479*time) );
cx = cx + 0.410*( 0.60*sin(0.374*time) + 0.40*sin(0.294*time) );
cy = cy + 0.410*( 0.60*sin(0.393*time) + 0.40*sin(0.223*time) );
wave_mystery = wave_mystery + 0.15*( 0.60*sin(0.629*time) + 0.40*sin(1.826*time) );
warp = warp*vol;
zoom = zoom - 0.02*zoom*bass_att;
zoom_exp = 1.5*( 0.60*sin(0.381*time) + 0.40*sin(0.479*time) );
ob_a = 1 - 2*vol;
monitor = zoom_exp;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 vec2 tmpvar_2;
 tmpvar_2 = (vec2(1.0, 0.0) * texsize.zw);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   (uv + tmpvar_2)
 ).xyz), texture (sampler_main, (uv - tmpvar_2)).xyz) - 0.035);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (0.05 + (0.9 * uv));
 ret_2 = (abs((
   ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  - texture (sampler_main, uv_1).xyz)) * 6.0);
 ret_2 = (ret_2 * 1.333);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, uv + float2(1,0)*texsize.zw ).xyz);
    ret = max(ret, tex2D( sampler_main, uv - float2(1,0)*texsize.zw ).xyz);

    // darken over time
    ret -= 0.035;

    // add noise
    //float2 uv_noise = uv*texsize_noise_lq.zw*texsize.xy + rand_frame.xy;
    //ret += (tex2D(sampler_noise_lq, uv_noise)*2-1)*0.02;
}

comp_hlsl:
shader_body
{
    uv = 0.05 + 0.9*uv;
    ret = tex2D(sampler_main, uv).xyz;
      // SUPER GLOW EDGES - looks awesome w/octopus
