 vec4 tmpvar_5;
 tmpvar_5.w = 0.0;
 tmpvar_5.xyz = max (tmpvar_4, (texture (sampler_main, (uv - tmpvar_2)) * 0.97)).xyz;
 vec4 tmpvar_6;
 tmpvar_6.w = 0.0;
 tmpvar_6.xyz = max (tmpvar_5, (texture (sampler_main, (uv + 
   (tmpvar_2 * 2.0)
 )) * 0.9)).xyz;
 ret_1 = (max (tmpvar_6, (texture (sampler_main, 
   (uv - (tmpvar_2 * 2.0))
 ) * 0.9)).xyz * 0.95);
 vec4 tmpvar_7;
 tmpvar_7.w = 1.0;
 tmpvar_7.xyz = ret_1;
 ret = tmpvar_7.xyz;
}

warp_hlsl:
shader_body
{

//Geiss's Motion Blur

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // this vector points exactly one pixel, in the direction of motion
    float2 v = normalize(uv-uv_orig)*texsize.zw;

    float3 s;
    ret = max(ret, tex2D(sampler_main, uv+v)*0.97);
    ret = max(ret, tex2D(sampler_main, uv-v)*0.97);
    ret = max(ret, tex2D(sampler_main, uv+v*2)*0.90);
    ret = max(ret, tex2D(sampler_main, uv-v*2)*0.90);

    // darken over time
    ret *= 0.95;

