   ((tmpvar_3 * (1.0 - (3.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (3.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.02);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (ret_1 * 4.5);
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, ((texture (sampler_main, tmpvar_2).xyz - 
   ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1)
 ) * 4.5), vec3(0.5, 0.5, 0.5));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-3*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+3*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.02;

    // add noise
    //float2 uv_noise = uv*texsize_noise_lq.zw*texsize.xy + rand_frame.xy;
    //ret += (tex2D(sampler_noise_lq, uv_noise)*2-1)*0.02;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 - GetBlur1(uv);
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    float3 ret2 = tex2D(sampler_main, uv_echo).xyz - GetBlur1(uv_echo);
    ret *= 4.5;
    ret2 *=4.5;
   ret = lerp(ret,ret2,0.50);

}
