shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = ((max (
   max (texture (sampler_main, uv).xyz, texture (sampler_main, ((tmpvar_3 * (1.0 - 
     (3.0 * sqrt(tmpvar_2))
   )) + 0.5)).xyz)
 , texture (sampler_main, 
   ((tmpvar_3 * (1.0 + (3.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
