comp:
shader_body { 
 vec2 uv_1;
 vec2 uv3_2;
 vec2 uv2_3;
 vec3 ret_4;
 uv_1 = (uv - 0.5);
 uv_1 = (uv_1 * aspect.xy);
 uv2_3.x = ((uv_1.x * -0.497265) - (uv_1.y * 0.8675987));
 uv2_3.y = ((uv_1.x * 0.8675987) + (uv_1.y * -0.497265));
 uv3_2.x = ((uv_1.x * -0.5000263) - (uv_1.y * -0.8660102));
 uv3_2.y = ((uv_1.x * -0.8660102) + (uv_1.y * -0.5000263));
 ret_4 = (max (max (texture (sampler_main, 
   (uv_1 + 0.5)
 ).xyz, texture (sampler_main, 
   (uv2_3 + 0.5)
 ).xyz), texture (sampler_main, (uv3_2 + 0.5)).xyz) * 1.3);
 ret_4 = (ret_4 * ((hue_shader * 4.0) - 2.0));
