warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret = (ret-0.034)*0.8;
}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

    ret *= 1.3;
//    ret *= hue_shader*4-2;

}
