shader_body
{
    uv -= 0.5;
    uv *= (1 + q1*uv.y + q2*uv.x);
    uv += 0.5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;


    ret = max(ret,  tex2D(sampler_main, uv + (ret.xy-0.5)*texsize.zw*13*float2(0,1)).xyz );   

