warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xxx - 0.5)
  / 256.0) * 2.0));
 vec2 x_2;
 x_2 = (uv - uv_orig);
 ret_1 = (ret_1 * (0.97 + (0.03 * 
   clamp ((sqrt(dot (x_2, x_2)) * 200.0), 0.0, 1.0)
 )));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1 = texture (sampler_main, uv);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (pow (mix (vec3(
   dot (tmpvar_1.xyz, vec3(0.3, 0.5, 0.2))
 ), tmpvar_1.xyz, vec3(1.7, 1.7, 1.7)), vec3(0.5, 0.7, 1.3)) * 1.5);
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xxx-0.5)/256.0*2;

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    float lum = dot(ret, float3(0.3,0.5,0.2));

    // super-saturate color:
    ret = lerp(lum, ret, 1.7);

    // add some stripes:
    //ret += 0.04*cos(lum*29+2.8);

    // assuming input is mostly greyscale, apply a color palette:
    ret = pow(ret,float3(0.5,0.7,1.3));
    ret *= 1.5; // a little bit of overbright
}
