 ret_1 = -(texture (sampler_main, uv).xyz);
 ret_1 = (ret_1 + (clamp (
   ((dot (texture (sampler_main, (uv + tmpvar_2.xz)).xyz, vec3(0.32, 0.49, 0.29)) - dot (texture (sampler_main, (uv - tmpvar_2.xz)).xyz, vec3(0.32, 0.49, 0.29))) * 64.0)
 , 0.0, 1.0) * vec3(1.0, 0.6, 0.1)));
