warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_fc_main, uv).xyz * 0.5);
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.5));
 ret_1 = (ret_1 * (ret_1 / (0.975 * ret_1)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz * (fract(
   (tmpvar_2.xyz * 4.0)
 ) * 2.0));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz*.5;
    ret += GetBlur1(uv)*.5;

    ret *= ret/(.975*ret); //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;

    ret *= frac(ret*4)*2;
    //ret += GetBlur1(uv) * .1;
    ret *= ret; 
}
