frame_eqs_eel:
vol=(bass+mid+treb_att)*.3333;

redsine=.5+.25*bass*sin(time*3);
greensine=.5+.25*mid*sin(time*2);
bluesine=.5+.25*treb*sin(time);

redif=if(above(bass,1.2),redsine,if(above(redif,.95),0,redif*.85));
greenif=if(above(mid,1.2),greensine,if(above(greenif,.95),0,greenif*.85));
blueif=if(above(treb,1.2),bluesine,if(above(blueif,.95),0,blueif*.85));

wave_r=redif;
wave_g=greenif;
wave_b=blueif;

q1=vol;

pixel_eqs_eel:
zoom=zoom+.08*treb_att;

zoom = zoom-.05*rad*above(zoom,rad);

//rot=.01*sin(time*-.98);

cx=.5+.35*sin(time*.98);
cy=.5+.35*sin(time*.62);

dx=.01*sin(time*.44+ang);
dy=.01*sin(time*.579-ang);

//rot = rot-.025*sin(rad);

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_fc_main, uv).xyz * 0.5);
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.5));
 ret_1 = (ret_1 * (ret_1 / (0.98 * ret_1)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz * (fract(
   (tmpvar_2.xyz * 4.0)
 ) * 2.0));
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.1));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz*.5;
    ret += GetBlur1(uv)*.5;

    // darken (decay) over time and distance from center
    ret *= ret/(.98*ret); //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;

    ret *= frac(ret*4)*2;
    ret += GetBlur1(uv) * .1;
    ret *= ret; 
}
