waves.frame_eqs_eel:
ba=bass-.9;
tr=treb-.9;
mi=mid-.9;
xx=xx+ba*.01-tr*.001;
yy=yy+tr*.01-ba*.001;

xx=xx+cos(frame*6.28+bass-1)*.05*q2;
yy=yy+sin(time*3.14*q6)*.05*q4;

xx=if(above(1,xx),if(below(0,xx),xx,1),0);
yy=if(above(1,yy),if(below(0,yy),yy,1),0);

t1=xx;
