 tmpvar_2 = texture (sampler_fc_main, uv);
 ret_1 = (tmpvar_2.xyz + ((
   (tmpvar_2.xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1))
  * 0.1) - 0.015));
 ret_1.y = ret_1.y;
 vec2 tmpvar_3;
