warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.1 - 0.015;
//    ret *= 0.94;

   // add noise:
   float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.1;// + rand_frame.xy;
   ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.0;


    float2 zoom =1.85;
    float2 c = float2(0.448,0.701);
    float2 my_uv = (uv_orig+(uv-uv_orig)*.4-0.5)*zoom;
    my_uv = float2(my_uv.x*my_uv.x - my_uv.y*my_uv.y, 2*my_uv.x*my_uv.y) + c;

   ret.z =tex2D(sampler_fc_main, my_uv).z + 0.004;

float2 uv_x = 0.5 + (lerp(uv_orig,uv,-1)-0.5)*1.00;
ret.x = tex2D(sampler_fc_main,uv_x).x - 0.00;
}

