}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   ((uv - 0.5) * vec2(-1.0, 1.0))
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * (4.6 / rad));
 ret_1 = (ret_1 * ret_1);
 ret_1 = (((
   (vec3(1.1, 0.8, 0.7) * ret_1.x)
  * 
   (1.1 + sin((time * 0.83)))
 ) + (
   (vec3(1.0, 0.7, 0.65) * ret_1.y)
  * 
   (1.1 + sin((time * 1.3)))
 )) + ((vec3(1.0, 0.8, 0.7) * ret_1.z) * (1.1 + 
   sin((time * 0.92))
 )));
 ret_1 = (ret_1 - (1.0/(rad)));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.95+0.5 ).z;

    // .x = fastest layer, .z = slowest
    // slowly shift stuff toward the front, over time:
    ret += float3(-ret.x,ret.x-ret.y,ret.y-ret.z)*0.03;

    // ERROR DIFFUSION DITHER - looks great
    //float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    //ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*3;

    // darken over time
    ret -= 0.003;
    ret *= 0.99;
    ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*1*float2(-1,1) + .5;
    ret = max(ret, tex2D(sampler_main, uv + 0.06*float2(cos(uv.y*27*0.5),sin(uv.x*39*0.5))).xyz);
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo

    ret *= 4.60/rad; //gamma
    ret *= ret; //darken
