frame_eqs_eel:
vol = bass*8 + mid*5 + treb*3;
m = m*0.97 + vol*0.08;
monitor = vol;
beat = above(vol,res)*above(vol,m)*above(vol,16);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.1) + (1-beat)*(res -  (0.1+diff*0.02)*60/fps);
res = max(0,res);


x = if(beat,rand(60)*0.01 + 0.2,x);
y = if(beat,rand(60)*0.01 + 0.2,y);
q1 = x;
q2 = y;

q4 = aspectx;
q5 = aspecty;
wave_x = q1;
wave_y = 1-q2;

pixel_eqs_eel:
cx = q1;
cy = q2;

d = pow(sqrt(sqr(x-cx)+sqr(y-cy)),0.8);
r = 0.2;
v = 0.2;

dx = (x - cx)*v*(d-r);
dy = (y - cy)*v*(d-r);

