warp_hlsl:
shader_body
{
    // sample previous frame 3 times, creating radial blur
    float2 v = normalize(uv - 0.5);
    //v = v.yx * float2(1,-1);
    v *= texsize.zw;
    ret = 0.2*(   tex2D( sampler_main, uv ).xyz
                   +  tex2D(sampler_main, uv - v*2.5 ).xyz
                   +  tex2D(sampler_main, uv - v*5.5 ).xyz
                   +  tex2D(sampler_main, uv - v*9  ).xyz
                   +  tex2D(sampler_main, uv - v*13 ).xyz
    );

    // darken over time
    ret = (ret-0.01)*0.975; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

    ret *= 1.2;
    ret *= hue_shader*4-2;

}
