#define MyGet GetPixel //GetBlur1
shader_body
{
    // GROW EFFECT - bright pixels spread radially outward.
    // BE CAREFUL - this can really thrash the texture cache! (SLOW)
    float grad_rad = 3;  //TWEAK
    float str = 2.3;      //TWEAK
    float3 d = float3(texsize.zw, 0) * grad_rad;
    float4 lums = 0;
    lums.x = lum( MyGet(uv + texsize.zw*d.xz) );
    lums.y = lum( MyGet(uv - texsize.zw*d.xz) );
    lums.z = lum( MyGet(uv + texsize.zw*d.zy) );
    lums.w = lum( MyGet(uv - texsize.zw*d.zy) );
    float2 grad = float2(lums.x-lums.y, lums.z-lums.w)*str*1000/grad_rad;
    grad = (saturate(grad*0.5+0.5)*2-1)*1.4;
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv + grad*texsize.zw ).xyz;

    // darken (decay) over time
    ret *= 0.95; //or try: ret -= 0.004;
}

