warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, uv).xyz * vec3(0.989, 0.986, 0.984));
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 fret_1;
 vec2 uvp_2;
 uvp_2.x = uv.x;
 uvp_2.y = (uv.y * 0.38);
 fret_1 = (((1.0/(
   ((texture (sampler_blur2, uvp_2).xyz * scale2) + bias2)
 )) - (texture (sampler_main, uvp_2).xyz * 
   ((texture (sampler_blur1, uvp_2).xyz * scale1) + bias1)
 )) - ((
   (texture (sampler_blur2, uv).xyz * scale2)
  + bias2) * 0.55));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (1.0 - (fret_1 * fret_1));
 ret = tmpvar_3.xyz;
}

